import { useEffect, useState } from "react";
import axios from "axios";
import DataTable from "react-data-table-component";

function App() {
  const columns = [
    {
      name: "Исполнитель",
      selector: (row) => row.artist,
      sortable: true,
    },
    {
      name: "Песня",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Жанр",
      selector: (row) => row.genre,
      sortable: true,
    },
    {
      name: "Год",
      selector: (row) => row.year,
      sortable: true,
    },
  ];
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [perPage, setPerPage] = useState(10);

  useEffect(() => {
    const fetchAllTracks = async () => {
      try {
        const res = await axios.get("http://localhost:8880/tracks");
        console.log(res);
        setTracks(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllTracks();
  }, []);
  return (
    <div className="App">
      <div
        style={{
          width: "1000px",
        }}
      >
        <DataTable
          columns={columns}
          data={tracks}
          defaultSortFieldId={1}
          pagination
          style={{
            "display": "center"
          }}
        />
      </div>
    </div>
  );
}

export default App;
