import { useEffect, useState, useMemo } from "react";
import axios from "axios";
import DataTable from "react-data-table-component";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import styled from "styled-components";

const TextField = styled.input`
  height: 32px;
  width: 200px;
  border-radius: 3px;
  border-top-left-radius: 5px;
  border-bottom-left-radius: 5px;
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  border: 1px solid #e5e5e5;
  padding: 0 32px 0 16px;

  &:hover {
    cursor: pointer;
  }
`;

const FilterComponent = ({ filterText, onFilter, onClear, searchColumn }) => (
  <>
    <TextField
      id="search"
      type="text"
      aria-label="Search Input"
      value={filterText}
      onChange={onFilter}
      placeholder={searchColumn}
    />
  </>
);

function App() {
  const columns = [
    {
      name: "Исполнитель",
      selector: (row) => row.artist,
      sortable: true,
    },
    {
      name: "Песня",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Жанр",
      selector: (row) => row.genre,
      sortable: true,
    },
    {
      name: "Год",
      selector: (row) => row.year,
      sortable: true,
    },
  ];
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [perPage, setPerPage] = useState(10);
  const [searchName, setSearchName] = useState("");

  useEffect(() => {
    const fetchAllTracks = async () => {
      try {
        const res = await axios.get("http://localhost:8080/tracks");
        console.log(res);
        setTracks(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllTracks();
  }, []);
  const [filterTextArtist, setFilterTextArtist] = useState("");
  const [filterTextName, setFilterTextName] = useState("");
  const [resetPaginationToggle, setResetPaginationToggle] = useState(false);
  const filteredItems = tracks.filter(
    (item) =>
      item.artist &&
      item.artist.toLowerCase().includes(filterTextArtist.toLowerCase()) &&
      item.name &&
      item.name.toLowerCase().includes(filterTextName.toLowerCase())
  );
  const handleClearName = () => {
    if (filterTextName) {
      setResetPaginationToggle(!resetPaginationToggle);
      setFilterTextName("");
    }
  };

  const handleClearArtist = () => {
    if (filterTextArtist) {
      setResetPaginationToggle(!resetPaginationToggle);
      setFilterTextArtist("");
    }
  };

  return (
    <div className="App">
      <div
        style={{
          width: "1000px",
        }}
      >
        <DataTable
          title="Contact List"
          columns={columns}
          data={filteredItems}
          pagination
          paginationResetDefaultPage={resetPaginationToggle}
          selectableRows
          persistTableHead
        />
      </div>
      <div>
        <FilterComponent
          onFilter={(e) => setFilterTextArtist(e.target.value)}
          onClear={handleClearArtist}
          filterText={filterTextArtist}
          searchColumn = "Search by Artist"
        />

        <FilterComponent
          onFilter={(e) => setFilterTextName(e.target.value)}
          onClear={handleClearName}
          filterText={filterTextName}
        />
      </div>
    </div>
  );
}

export default App;
