import { useEffect, useState, useMemo } from "react";
import axios from "axios";
import DataTable from "react-data-table-component";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";

function App() {
  const columns = [
    {
      name: "Исполнитель",
      selector: (row) => row.artist,
      sortable: true,
    },
    {
      name: "Песня",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Жанр",
      selector: (row) => row.genre,
      sortable: true,
    },
    {
      name: "Год",
      selector: (row) => row.year,
      sortable: true,
    },
  ];
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [perPage, setPerPage] = useState(10);
  const [searchName, setSearchName] = useState("");

  useEffect(() => {
    const fetchAllTracks = async () => {
      try {
        const res = await axios.get("http://localhost:8080/tracks");
        console.log(res);
        setTracks(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllTracks();
  }, []);
  const [filterText, setFilterText] = useState("");
  const [resetPaginationToggle, setResetPaginationToggle] =
    useState(false);
  const filteredItems = tracks.filter(
    (item) =>
      item.artist && item.artist.toLowerCase().includes(filterText.toLowerCase())
  );

  const subHeaderComponentMemo = useMemo(() => {
    const handleClear = () => {
      if (filterText) {
        setResetPaginationToggle(!resetPaginationToggle);
        setFilterText("");
      }
    };

    return (
      <input
        onFilter={(e) => setFilterText(e.target.value)}
        onClear={handleClear}
        filterText={filterText}
      />
    );
  }, [filterText, resetPaginationToggle]);

  return (
    <div className="App">
      <div
        style={{
          width: "1000px",
        }}
      >
        <DataTable
          title="Contact List"
          columns={columns}
          data={filteredItems}
          pagination
          paginationResetDefaultPage={resetPaginationToggle} 
          subHeader
          subHeaderComponent={subHeaderComponentMemo}
          selectableRows
          persistTableHead
        />
      </div>
      <div>
        <Form>
          <InputGroup className="my-3">
            <Form.Control
              onChange={(e) => {
                setSearchName(e.target.value);
                const data = tracks.filter((item) => {
                  return searchName.toLowerCase() === ""
                    ? item
                    : item.artist.toLowerCase.includes(searchName);
                  console.log(item);
                });
                // console.log(data)
              }}
              placeholed="Search"
            />
          </InputGroup>
        </Form>
      </div>
    </div>
  );
}

export default App;
